mapper.update:

update yc_user_group
set user_id = #{userId,jdbcType=BIGINT},
  group_id = #{groupId,jdbcType=BIGINT},
  group_role = #{groupRole,jdbcType=INTEGER},
  entry_date = #{entryDate,jdbcType=TIMESTAMP}
where user_group_id = #{userGroupId,jdbcType=BIGINT}

mapper.select:

select user_group_id, user_id, group_id, group_role, entry_date
from yc_user_group
where user_group_id = #{userGroupId,jdbcType=BIGINT}

select count(*)
from yc_user_group
where group_id = #{groupId}

select count(*)
from yc_user_group
where group_id = #{groupId} and user_id = #{userId}

select count(*)
from yc_user_group
where user_id = #{userId}

select group_id
from yc_user_group
where user_id = #{userId}

mapper.update:
update yc_user_group
set group_role = 2
where user_id = #{userId} and group_id = #{groupId}

update yc_user_group
set group_role = 1
where user_id = #{userId} and group_id = #{groupId}
